import React, { Component } from 'react';
import Form from './Form';


const API_KEY = "b5eaa19399377445bfddb5e2edce42e9";



class Weather extends Component {
    state={
        city:undefined,
        length:undefined
    }
    getWeather = async (e) => {
        e.preventDefault();
        const city = e.target.elements.city.value;
        const country = e.target.elements.country.value;
       // const api_call = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=${API_KEY}&units=metric`);
       const api_call = await fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${city},${country}&appid=${API_KEY}&units=metric`);
       // const data = await api_call.json();
        const data = await api_call.json();
        if (city && country) {
          //console.log(data);
          console.log("in weatherjs");
          console.log(data);
          const dt = data.list[0].dt_txt.split(" ");
          const apicity = data.city.name;
          const length1 = data.list.length;
          console.log(dt)
          const ct=dt[0].substring(0,10);
          var days=[dt[0]];
          console.log(ct);
          for(var i=0;i<40;i++){
            if(data.list[i].dt_txt.substring(0,10)!= ct){
                var d = data.list[i].dt_txt.substring(0,10);
                days.push(d);
            }
            }
          console.log(days);
          this.setState({
              city: apicity,
              length :length1
          });

        } else {

        }
      }
    render() { 
        //console.log(this.props.json1);

       // console.log(d.city.name);
        return (  

            <div>
            <Form getWeather={this.getWeather}/> 
            <p>City:{this.state.city}</p>
            <p>Date:{this.props.date}</p>
            <p>Time:{this.props.time}</p>
            <p>temp:{this.props.temp}</p>
            <p>length:{this.state.length}</p>
            <p>Day1:</p>
            <p>Day2:</p>
            <p>day3:</p>
            <p>day4:</p>
            <p>day5:</p>


            </div> );
    }
}

export default Weather;
