import React, { Component } from 'react';

//import 'weather-icons/css/weather-icon.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../../node_modules/weather-icons/css/weather-icons.css'
class Template extends Component {
    state = {
        show:false,
        count:0
      }
      show_hide = async(e) =>{

        console.log(document.getElementsByName("date")[0].innerHTML);
        console.log("inside func");

        console.log(this.props.forecast.date_text);


      }
    render() { 
        // {console.log("in temp component")}
        { console.log(this.props.forecast)}
        {this.props.forecast.slno<=4? this.setState({
            show : !this.state.show
        }):null}

        return ( <div>


           {

               this.props.forecast.slno<=4 &&this.state.show?
               <div onClick = {()=>this.show_hide()}>       
               <i  className="wi wi-day-sunny display-1" ></i>
               <p name ="date">Date:{this.props.forecast.date}</p>
               {this.props.time && <p>Time:{this.props.time}</p>}
               <p>Temprature:{this.props.forecast.temp}</p>
               <p>Max:{this.props.forecast.max_temp}</p>
               <p>Min:{this.props.forecast.min_temp}</p>
               <p>Description:{this.props.forecast.description}</p>
               </div>:null

           }




            </div> );
    }
}

export default Template;
